sys_script_include.script:
var SIAMUtil = Class.create();

/*
 * Returns true if the given item is null, undefined, or evaluates to the empty string.
 */
SIAMUtil.nil = function(item) {
	var str = item + '';

	return (str == null) || (typeof str == 'undefined') || ('' == '' + str);
};

SIAMUtil.notNil = function(item) {
	return (!SIAMUtil.nil(item));
};

SIAMUtil.isJSON = function(item) {
	item = typeof item !== "string"
	? JSON.stringify(item)
	: item;

	try {
		item = JSON.parse(item);
	} catch (e) {
		return false;
	}

	if (typeof item === "object" && item !== null) {
		return true;
	}

	return false;

};

/*
 * Helper script which will traveres a JSON structure and add parent nodes
 */
SIAMUtil.setParent = function(obj){
	for (var key in obj) {
		if (obj.hasOwnProperty(key)) {
			var current = obj[key];
			if (typeof current === 'object' && key != "parent") {
				current.parent = obj;
				SIAMUtil.setParent(current);
			}
		}
	}

};
